import React, { Component } from "react";
import NewsItems from "./NewsItems";

export class News extends Component {
  articles = [];
  constructor() {
    super();
    // console.log("This is my Constructor.");

    this.state = {
      articles: this.articles,
      loading: false,
      page: 1,
      pageSize: 10
    };
  }
  async componentDidMount(){
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=43596fac69da4e19bd989984ab1f1dfd&pageSize=${this.state.pageSize}&page=${this.state.page}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      articles: parsedData.articles,
      totalResults: parsedData.totalResults
    });

   
  }

  preClick = async () => {
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=43596fac69da4e19bd989984ab1f1dfd&pageSize=${this.state.pageSize}&page=${this.state.page-1}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      articles: parsedData.articles,
      page: this.state.page-1,
      totalResults: parsedData.totalResults
    });
    
  }
  nextClick = async () => {
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=43596fac69da4e19bd989984ab1f1dfd&pageSize=${this.state.pageSize}&page=${this.state.page+1}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    // console.log(parsedData);
    this.setState({
      articles: parsedData.articles,
      page: this.state.page+1,
      totalResults: parsedData.totalResults
    });
    
  }
  render() {
   
    return (
      <div className="container my-3">
        <h3>NewsMonkey - headlines</h3>

        <div>
          <div className="row">
            {this.state.articles.map((element) => {
              return(
                <div className="col-md-4" key={element.url}>
                  <NewsItems
                    title={element.title?element.title: " "}
                    description={element.description?element.description: " "}
                    imgUrl={element.urlToImage?element.urlToImage:'https://t4.ftcdn.net/jpg/02/00/40/11/360_F_200401146_cNHcKZToONyw6t0pXDKXfgeFj8OBE23g.jpg'}
                    newsUrl={element.url}
                  />
                </div>
              );
              
            })}
          </div>
        </div>
        <div className="container d-flex justify-content-between">
          <button disabled={this.state.page<=1} className="btn btn-dark" onClick={this.preClick}>Previous</button>
          <button disabled={this.state.page>=Math.ceil(this.state.totalResults/this.state.pageSize)} className="btn btn-dark" onClick={this.nextClick}>Next</button>
        </div>
      </div>
    );
  }
}

export default News;
