import "./App.css";
import React, { Component } from "react";
import Navbar from "./components/Navbar";
import News from "./components/News";

// import * as ReactDOM from "react-dom/client";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import LoadingBar from "react-top-loading-bar";

export default class App extends Component {
  pageSize = 9;

  state = {
    progress: 0
  }
  setProgress(progress){
    this.setState({progress: progress})
  }

  render() {
    return (
      <BrowserRouter>
        <LoadingBar
          color="#f11946"
          progress={this.state.progress}

        />
        <Routes>
          <Route
            exact
            path="/"
            element={
              <div>
                <Navbar />
                <News
                  pageSize={this.pageSize}
                  country="in"
                  key="general"
                  category="general"
                />
              </div>
            }
          />
          <Route
            exact
            path="/about"
            element={
              <div>
                <Navbar />
                <News
                  pageSize={this.pageSize}
                  country="in"
                  key="about"
                  category="general"
                />
              </div>
            }
          />
          <Route
            exact
            path="/business"
            element={
              <div>
                <Navbar />
                <News
                  pageSize={this.pageSize}
                  country="in"
                  key="business"
                  category="business"
                />
              </div>
            }
          />
          <Route
            exact
            path="/entertainment"
            element={
              <div>
                <Navbar />
                <News
                  pageSize={this.pageSize}
                  country="in"
                  key="entertainment"
                  category="entertainment"
                />
              </div>
            }
          />
          <Route
            exact
            path="/health"
            element={
              <div>
                <Navbar />
                <News
                  pageSize={this.pageSize}
                  country="in"
                  key="health"
                  category="health"
                />
              </div>
            }
          />
          <Route
            exact
            path="/science"
            element={
              <div>
                <Navbar />
                <News
                  pageSize={this.pageSize}
                  country="in"
                  key="science"
                  category="science"
                />
              </div>
            }
          />
          <Route
            exact
            path="/sports"
            element={
              <div>
                <Navbar />
                <News
                  pageSize={this.pageSize}
                  country="in"
                  key="sports"
                  category="sports"
                />
              </div>
            }
          />
          <Route
            exact
            path="/technology"
            element={
              <div>
                <Navbar />
                <News
                  pageSize={this.pageSize}
                  country="in"
                  key="technology"
                  category="technology"
                />
              </div>
            }
          />
        </Routes>
      </BrowserRouter>
    );
  }
}
