import React, { Component } from "react";
import NewsItems from "./NewsItems";
import Loaading from './Loading'
import PropTypes from 'prop-types'


export class News extends Component {
  static defaultProps = {
    country: 'in',
    pageSize: 9,
    category: 'general',
    color: 'danger',
  }
  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
    color: PropTypes.string,
  }
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      // pageSize: 10
    };
  }
  async componentDidMount(){
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=43596fac69da4e19bd989984ab1f1dfd&pageSize=${this.props.pageSize}&page=${this.state.page}`;
    this.setState({loading: true});
    let data = await fetch(url);
    let parsedData = await data.json();
  
    this.setState({
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
      loading: false
    });

   
  }

  preClick = async () => {
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=43596fac69da4e19bd989984ab1f1dfd&pageSize=${this.props.pageSize}&page=${this.state.page-1}`;
    this.setState({loading: true});
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      loading: true
    });
    this.setState({
      articles: parsedData.articles,
      page: this.state.page-1,
      totalResults: parsedData.totalResults,
      loading: false
    });
    
  }
  nextClick = async () => {
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=43596fac69da4e19bd989984ab1f1dfd&pageSize=${this.props.pageSize}&page=${this.state.page+1}`;
    this.setState({loading: true});
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      loading: true
    })
    this.setState({
      articles: parsedData.articles,
      page: this.state.page+1,
      totalResults: parsedData.totalResults,
      loading: false
    });
    
  }
  render() {
   
    return (
      <div className="container my-3">
        <h3 className="text-center" style={{margin: '20px 0px'}}>NewsMonkey - headlines</h3>
        {this.state.loading && <Loaading />}
        <div>
          <div className="row">
            {!this.state.loading && this.state.articles.map((element) => {
              return(
                <div className="col-md-4" key={element.url}>
                  <NewsItems
                    title={element.title?element.title: " "}
                    description={element.description?element.description: " "}
                    imgUrl={element.urlToImage?element.urlToImage:'https://t4.ftcdn.net/jpg/02/00/40/11/360_F_200401146_cNHcKZToONyw6t0pXDKXfgeFj8OBE23g.jpg'}
                    newsUrl={element.url}
                    author={element.author}
                    publishedAt={element.publishedAt}
                    source={element.source.name}
                    color={this.color}
                  />
                </div>
              );
              
            })}
          </div>
        </div>
        <div className="container d-flex justify-content-between">
          <button disabled={this.state.page<=1} className="btn btn-dark" onClick={this.preClick}>Previous</button>
          <button disabled={this.state.page>=Math.ceil(this.state.totalResults/this.props.pageSize)} className="btn btn-dark" onClick={this.nextClick}>Next</button>
        </div>
      </div>
    );
  }
}

export default News;
